INSERT INTO `card` VALUES (564,16,6,NULL,NULL,'2018-01-05 18:07:56','15020',20,4,'Someone Else\'s Problem',NULL,'<b>React:</b> After you mark a location for a job, choose an opposing dude. That dude cannot join the mark’s posse and is not affected by the job effects.','\"That circus fella\'s been doin\' something wrong with the sick all this time!\" -Ulysses Marks\r\n\"So? The plague is Gomorra\'s problem, not ours.\" -Jonah Essex','Teejay Ralph Villahermosa',0,10,NULL,NULL,NULL,NULL,NULL,NULL,'8ac302b6-711f-41dc-9f0d-4ec19f16f813',0);
INSERT INTO `card` VALUES (565,16,6,NULL,NULL,'2016-05-12 20:11:40','15021',21,4,'Lost to the Plague','Condition','This dude can only move to adjacent locations and home, and cannot boot to pay the cost of abilities.\r\nIncrease this dude’s upkeep by 1 for each other Condition they have.\r\n<b>Noon:</b> Attach to any dude with value 6 or lower.',NULL,'Ikaan Studio',1,11,NULL,NULL,NULL,NULL,NULL,NULL,'c22ec64d-d7d6-4c06-b09d-4a827c0dbad3',0);
