INSERT INTO `card` VALUES (567,17,3,1,5,'2018-01-05 18:09:31','16002',2,4,'Jim Hexter',NULL,'<b>Shootout:</b> Pay 1 ghost rock to the controller of the opposing posse’s leader or mark to make Jim a stud.','\"He\'s a master burglar like you\'ve never seen. Of course, with all the bribe money he paid out, I think Morgan just about broke even.\" -T\'ou Chi Chow','Biagio D\'Alessandro',4,11,1,NULL,3,1,NULL,NULL,'b1d4638a-0118-426f-a966-1a9c6ff0da5b',1);
