INSERT INTO `card` VALUES (598,18,4,NULL,NULL,'2017-12-06 17:14:07','17012',12,4,'Knight\'s Chasuble','Attire • Mystical','This dude’s value cannot be reduced.\r\nConditions cannot be attached to this dude.\r\n<b>React, Boot:</b> After this dude is booted by an opposing ability, unboot them.',NULL,'Hinkle/Silva Art',1,5,NULL,NULL,NULL,1,NULL,NULL,'92cf4c1a-cd1b-4377-a0dd-e06d52edbbff',0);
