INSERT INTO `card` VALUES (602,18,5,NULL,NULL,'2016-08-06 22:25:16','17016',16,4,'Get Behind Me, Satan!','Miracle','<b>Resolution Miracle 5, Boot:</b> Reduce your casualties this round by this dude’s Blessed rating. Choose an opposing Abomination dude, who provides nothing to the posse’s stud and draw ratings during the next round of the shootout.',NULL,'Biagio D\'Alessandro',1,3,NULL,NULL,NULL,NULL,NULL,NULL,'f5359fcb-0095-4ef0-8264-e3eec905528d',1);
INSERT INTO `card` VALUES (603,18,5,NULL,NULL,'2016-08-06 22:26:01','17017',17,4,'Ghostly Communion','Spirit','<b>Noon Spirit 5, Boot:</b> Move this dude to a Holy Ground location or to a location adjacent to one.\r\n<b>Shootout Spirit 7, Boot:</b> This dude joins your posse at a Holy Ground location or a location adjacent to one. This dude becomes a stud.',NULL,'Ikaan Studio',0,12,NULL,NULL,NULL,NULL,NULL,NULL,'7469036b-d154-4cb5-9eff-7d77fbdbcdf0',1);
