INSERT INTO `card` VALUES (607,18,6,NULL,NULL,'2016-08-06 22:30:07','17021',21,4,'All Or Nothing',NULL,'<b>Noon Job:</b> Mark a location. Dudes at that location can join the defending posse without permission from the mark. If successful, discard all cards attached to the location. If it’s a deed or the town square, discard all wanted and Abomination dudes at the location, and send the rest home booted.','\"The time for talk is over!\" -Abram Grothe','Marcel Stobinski',1,12,NULL,NULL,NULL,NULL,NULL,NULL,'22e8401c-bf60-42e0-8119-36ce05c5d893',0);
