INSERT INTO `card` VALUES (612,19,1,NULL,3,'2016-10-13 19:06:55','18005',5,1,'Morgan Regulators',NULL,'When checking for victory, if there has not been a shootout this turn, each player\'s influence total at each location is limited to 5.\r\n<b>Noon, Boot:</b> Choose your dude. Move them to the town square. That dude becomes a stud. If they have a Horse, unboot them.',NULL,'Mirco Paganessi',NULL,NULL,NULL,3,NULL,NULL,NULL,19,'b94ed693-16ed-4c7c-84d8-375498922a74',1);
