INSERT INTO `card` VALUES (622,19,3,2,2,'2016-10-13 19:24:08','18015',15,4,'Father Tolarios','Deputy • Blessed 2','<b>Noon:</b> Discard a card. Choose an opposing wanted dude at this or an adjacent location. Lower that dude\'s bounty by 1 to search your deck or discard pile for a non-Unique Miracle or non-Unique Mystical goods, reveal it, and add it to your hand.',NULL,'Riccardo Rullo',4,5,1,NULL,0,1,NULL,NULL,'c213a4b2-95ee-487c-b0a8-8cc09ac87a7d',0);
