INSERT INTO `card` VALUES (643,19,6,NULL,NULL,'2017-12-13 13:40:24','18036',36,4,'We Got A Beef!',NULL,'<b>React:</b> After an unbooted dude accepts a callout, but before posses are formed, swap the location of either the mark or leader with a dude at an adjacent location controlled by the same player. The chosen dude replaces the original mark or leader in the shootout.',NULL,'Ikaan Studio',1,4,NULL,NULL,NULL,NULL,NULL,NULL,'e4118ee1-a9eb-4738-a378-673ca6c918a7',0);
INSERT INTO `card` VALUES (644,19,6,NULL,NULL,'2016-10-13 19:58:13','18037',37,4,'Great Sage Matching Heaven','Technique • Tao of Bull Demon King','<b>Shootout Tao Technique:</b> This dude gets +1 Influence and gains the Harrowed keyword. <b>Combo</b> if your posse has equal or higher influence than the opposing posse.',NULL,'Mirco Paganessi',0,6,NULL,NULL,NULL,NULL,NULL,NULL,'39fa07ab-40d4-4ddd-b2a6-6c444a1f48cd',1);
