INSERT INTO `card` VALUES (648,19,3,1,2,'2016-10-24 18:56:58','18041',41,4,'\"Thunder Boy\" Nabbe','Deputy','<b>Repeat Shootout:</b> If there is a wanted dude in the opposing posse, Nabbe gets a bullet bonus equal to his influence and becomes a stud. You can use this ability once per shootout.',NULL,'Inna Vjuzhanina',3,6,1,NULL,1,2,NULL,NULL,'1c4fd81f-8b08-4e7f-9325-8c63104f2694',1);
