INSERT INTO `card` VALUES (677,20,4,NULL,NULL,'2017-11-16 02:51:24','19031',31,4,'Forsaken Hound','Sidekick','<b>Resolution, Boot:</b> This dude and an opposing dude cannot flee this round.','\"He\'s back, as if it never happened.\" - Lucy Clover\r\n\r\n\"Wouldn\'t be the first time.\" - Black Jack','Paul (Prof.) Herbert',2,11,NULL,NULL,NULL,NULL,NULL,NULL,'1ae574d7-9dd2-4999-a73f-90008198c1b9',0);
INSERT INTO `card` VALUES (678,20,2,NULL,NULL,'2017-11-16 02:57:50','19025',25,4,'The Oriental Saloon','Private • Casino • Saloon','Determine control of this deed using bullets instead of influence.\r\n\r\n<b>Controller React, Boot:</b> After you reveal a cheatin\' hand, unboot your dude in town and give that dude 2 bounty.','\"Just wanted to let you know, you\'re sitting in my chair...\"','Wayne Miller',3,10,NULL,1,NULL,NULL,1,NULL,'162aaf13-fe99-4bb6-8fbe-9587d71bd666',0);
INSERT INTO `card` VALUES (679,20,3,2,3,'2017-12-13 13:43:43','19013',13,4,'Adrián Vallejo',NULL,'<b>React:</b> After a card ability moves a dude to a shootout location and they join a posse, if Adrián has a Horse, he joins your posse <i>(moving if necessary)</i>.','\"I don\'t bow or do favors. I do hunt varmints with Chags, though.\"','Ron Spencer',5,7,1,NULL,2,1,NULL,NULL,'3d38ea0c-31d3-456e-a7cd-f7a66476395d',0);
INSERT INTO `card` VALUES (680,20,5,NULL,NULL,'2017-09-06 18:24:21','19038',38,4,'Intercession','Miracle','<b>React Miracle 5, Boot:</b> After an opposing dude becomes a stud, make one of your dudes a stud.\r\n\r\n<b>Shootout Miracle 7, Boot:</b> Choose your dude and an opposing dude. Each of those dudes unboots and gets +2 bullets.',NULL,'Lorenzo Sperlonga',1,13,NULL,NULL,NULL,NULL,NULL,NULL,'f638e171-5064-4c25-b1b7-7e5d762025b1',0);
