INSERT INTO `card` VALUES (686,20,3,1,1,'2017-10-10 22:56:58','19010',10,4,'Rosenbaum\'s Golem','Abomination','<b>Shootout, Boot:</b> Choose an opposing dude. Boot that dude, and you may give them +5 value. If that dude\'s grit is 11 or higher, you may unboot one of your other dudes.','\"Don\'t think bullets are going to help with this one...\"','Chris Bivins',2,10,0,NULL,0,0,NULL,NULL,'dafad1b5-7067-4efa-b063-67a4c5c2b42a',0);
