INSERT INTO `card` VALUES (723,7,4,NULL,NULL,'2015-11-14 19:51:48','00127',127,4,'Jael\'s Guile','Mystical • Weapon • Melee','<b>Cheatin’ Resolution, Boot:</b> If your hand is illegal, you must boot this dude to use this ability. Your opponent boots or discards a dude in their posse, then boots or discards a dude in their posse. Non-wanted dudes cannot be chosen if there are wanted dudes that can be chosen.',NULL,'Wayne Miller',1,10,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1);
INSERT INTO `card` VALUES (724,7,6,NULL,NULL,'2017-11-16 18:40:02','00128',128,4,'No Turning Back',NULL,'<b>Noon:</b> Ace a dude you own and control to gain ghost rock equal to their cost.\r\n<b>Resolution:</b> Ace a dude you own and control to reduce your casualties this round to zero. Choose a dude who gets +2 bullets and becomes a stud for the remainder of the Shootout. Your dudes cannot flee this round.','\"I knew it was a fight for life.\" - Wyatt Earp','Charles Urbach',0,12,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1);
