INSERT INTO `card` VALUES (726,7,4,NULL,NULL,'2015-10-02 20:23:26','00100',100,4,'Scoop Hound','Sidekick','<b>Shootout, Boot:</b> Shootout plays cannot bring dudes into either posse or send dudes home booted <i>(they remain in the posse without booting)</i>.','','Bryan Sevilla',2,10,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1);
