INSERT INTO `card` VALUES (802,21,4,NULL,NULL,'2018-03-20 12:16:29','20037',37,4,'Quarter Horse','Horse','<b>React, Boot:</b> After this dude’s posse succeeds at a job that didn’t mark your home, this dude stays at this location instead of going home booted.','\"Not the fastest, but always fearless.\" -Jarett Blake','Kamila Szutenberg',1,3,NULL,NULL,NULL,NULL,NULL,NULL,'ae22bba2-cf1e-4038-b7bb-1d3429c10034',0);
INSERT INTO `card` VALUES (803,21,4,3,NULL,'2022-01-26 22:56:02','20039',39,4,'Claws','Melee • Weapon','This dude is a stud.\r\n\r\nThis card cannot be attached or traded to a dude without the Harrowed or Abomination keyword. This card cannot be affected by opposing card effects.\r\n\r\nIf there is an unbooted non-Melee Weapon in the opposing posse, Claws provides no bullet bonus and does not make this dude a stud.',NULL,'Ikaan Studio',2,6,NULL,NULL,2,NULL,NULL,NULL,'ae22bba2-cf1e-4038-b7bb-1d3429c10036',0);
INSERT INTO `card` VALUES (804,21,4,3,NULL,'2018-04-28 03:10:45','20042',42,4,'Stone\'s Colt Dragoons','Mystical • Weapon','<b>React, Boot:</b> After an opposing dude joins this shootout by a card effect, boot that dude. That dude does not unboot at Sundown.','\"I know a fella who knows exactly what Stone\'s weakness is.\" -Coot Jenkins','MKultra Studio',1,13,NULL,NULL,1,NULL,NULL,NULL,'ae22bba2-cf1e-4038-b7bb-1d3429c10037',0);
